import React from 'react';
import './App.css';
import ProjectList from './components/ProjectList/ProjectList'

import { fetchProjects } from './services/fetchApi'

class App extends React.Component {
  constructor(){
    super()

    this.state = {
      projects: [],
      projectname: '', 
      description: '',
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount(){
    fetchProjects()
    .then((response) => {
      this.setState({ projects: response})
    })
  }

  handleChange(event) {
    this.setState({projectname: event.target.projectname, description: event.target.description});
  }

  render() {
    console.log("STATE", this.state.description)
    return (
      <div className="App">
        <ProjectList
          projects={this.state.projects}
        />
        <form>
          <label>
            Project name:
            <input type="text" name="projectname" projectname={this.state.projectname} />
          </label>
          <label>
            Project description:
            <input type="text" name="description" description={this.state.description}/>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    )
  }
}

export default App;
