import React from 'react';
import './App.css';
import ProjectList from './components/ProjectList/ProjectList'

import { FetchProjects, PostProject, DeleteProject } from './services/fetchApi'

class App extends React.Component {
  constructor(){
    super()

    this.state = {
      projects: [],
      projectname: '', 
      description: '',
    }

    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeDescription = this.handleChangeDescription.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleDeleteProject = this.handleDeleteProject.bind(this)
  }

  componentDidMount(){
    FetchProjects()
    .then((response) => {
      this.setState({ projects: response})
    })
  }

  handleChangeName(event) {
    this.setState({projectname: event.target.value });
  }

  handleChangeDescription(event) {
    this.setState({description: event.target.value });
  }

  handleSubmit(event){
    event.preventDefault();
    const { projectname, description } = this.state

    PostProject(projectname, description)
    .then((response) => {
      const newState = [...this.state.projects, response.data]
      this.setState({
        ...this.state,
        projects: newState
      })
    })
  }

  handleDeleteProject(event){
    const id = event.target.id
    DeleteProject(id)
    .then((response) => {
     console.log("response", response)
    })
  }

  render() {
    console.log("STATE", this.state.project)
    return (
      <div className="App">
        <ProjectList
          projects={this.state.projects}
          deleteTask={this.handleDeleteProject}
        />
        <form onSubmit={this.handleSubmit}>
          <label>
            Project name:
            <input type="text" name="projectname" value={this.state.projectname} onChange={this.handleChangeName} />
          </label>
          <label>
            Project description:
            <input type="text" name="description" value={this.state.description} onChange={this.handleChangeDescription}/>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    )
  }
}

export default App;
