import React from 'react';
import './App.css';
import ProjectList from './components/ProjectList/ProjectList'

import { FetchProjects, PostProject } from './services/fetchApi'

class App extends React.Component {
  constructor(){
    super()

    this.state = {
      projects: [],
      projectname: '', 
      description: '',
    }

    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeDescription = this.handleChangeDescription.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  componentDidMount(){
    FetchProjects()
    .then((response) => {
      this.setState({ projects: response})
    })
  }

  handleChangeName(event) {
    this.setState({projectname: event.target.value });
  }

  handleChangeDescription(event) {
    this.setState({description: event.target.value });
  }

  handleSubmit(event){
    event.preventDefault();
    const projectname = this.state.projectname
    const description = this.state.description
    PostProject(projectname, description)
    .then((response) => {
      console.log("JOEJOE", response)
    })

  }

  render() {
    return (
      <div className="App">
        <ProjectList
          projects={this.state.projects}
        />
        <form onSubmit={this.handleSubmit}>
          <label>
            Project name:
            <input type="text" name="projectname" value={this.state.projectname} onChange={this.handleChangeName} />
          </label>
          <label>
            Project description:
            <input type="text" name="description" value={this.state.description} onChange={this.handleChangeDescription}/>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    )
  }
}

export default App;
