import axios from 'axios'

import createAction from 'services/createAction';
import fetchApi from '../services/fetchApi';

const GET_PROJECTS = 'projects/GET_PROJECTS';
const GET_PROJECTS_SUCCESS = 'projects/GET_PROJECTS_SUCCES'
const GET_PROJECTS_FAILED = 'projects/GET_PROJECTS_FAILED'


const initialState = {
    data: {},
    loading: {
      get: false,
    },
    error: null,
  };

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case GET_PROJECTS:
            return {
              ...state,
              loading: {
                ...state.loading,
              },
              error: null,
            };
        case GET_PROJECTS_SUCCESS:
            return {
                ...state,
                loading: {
                    ...state.loading,
                },
                error: null,
            };
        case GET_PROJECTS_FAILED:
            return {
                    ...state,
                loading: {
                    ...state.loading,
                },
                error: null,
            };
      default:
        return state;
    }
  };  

export const fetchProjectsSuccess = createAction(GET_PROJECTS_SUCCESS)
export const fetchProjectsFailed = createAction(GET_PROJECTS_FAILED)


export const fetchProjects = () => (dispatch, getState) => new Promise((resolve) => {
    dispatch({ type: GET_PROJECTS });

    axios.get('http://localhost:9000/projects')
    .then((res) => {
        dispatch(fetchProjectsSuccess({ data: res.data }));
        resolve(res.data)
    })
    .catch((error) => {
        dispatch(getTeamFailed(error));
    })
  })
